import React, { useState } from 'react';
import './App.css';

function App() {
  const [formData, setFormData] = useState({ name: '', email: '' });
  const [status, setStatus] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus(''); // clear status message

    try {
      const response = await fetch('http://127.0.0.1:5000/api/subscribe', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });
      const result = await response.json();
      setStatus(result.message);
    } catch (error) {
      console.error(error);
      setStatus('Error subscribing. Please try again.');
    }
  };

  return (
    <div className="hero-container">
      {/* Background video */}
      <video autoPlay loop muted className="background-video">
        <source src="/background.mp4" type="video/mp4" />
        Your browser does not support HTML5 video.
      </video>

      {/* Overlay content */}
      <div className="overlay">
        {/* Navbar / Header */}
        <nav className="navbar">
          <div className="logo">YourLogo</div>
          <ul className="nav-links">
            <li><a href="#features">Features</a></li>
            <li><a href="#how-it-works">How it works</a></li>
            <li><a href="#testimonials">Testimonials</a></li>
            <li><a href="#faq">FAQ</a></li>
          </ul>
          <button className="notify-btn">Notify me</button>
        </nav>

        {/* Hero Section */}
        <div className="hero-content">
          <h1>Seamless Intake, Instant Insights</h1>
          <p>
            Our company specializes in AI-powered custom dynamic intake forms that adapt to user responses
            and generate comprehensive, easy-to-read summariesâ€”streamlining decision-making and enhancing efficiency.
          </p>

          {/* Subscription Form */}
          <form onSubmit={handleSubmit} className="subscribe-form">
            <input
              type="text"
              name="name"
              placeholder="Your name"
              value={formData.name}
              onChange={handleChange}
              required
            />
            <input
              type="email"
              name="email"
              placeholder="Your email"
              value={formData.email}
              onChange={handleChange}
              required
            />
            <button type="submit" className="subscribe-button">
              Get notified
            </button>
          </form>
          {status && <p className="status-message">{status}</p>}
        </div>
      </div>
    </div>
  );
}

export default App;
