// App.js
import React, { useState } from 'react';
import './App.css';

function App() {
  const [email, setEmail] = useState('');

  // Build image URLs using process.env.PUBLIC_URL
  const heroBg = process.env.PUBLIC_URL + '/HMsmQUMd8Orn91f43vOjeU9h0.jpg';
  const featureImg1 = process.env.PUBLIC_URL + '/Br0m8k72i6obkxvmy0bdrdCw50Y.jpg';
  const featureImg2 = process.env.PUBLIC_URL + '/lRrPICMkVKTOrJaQQBoCo6ZxxA.jpg';
  const featureImg3 = process.env.PUBLIC_URL + '/HnhpxTHTAwE18VvuvNHTwCtdiZ4.jpg';
  const teamImg = process.env.PUBLIC_URL + '/PUja2jlMhB77g7YskLJxlVzkBAA.jpg';

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Email submitted:', email);
    // Optionally call your Flask API endpoint here
  };

  return (
    <div className="App">
      {/* Hero Section */}
      <section
        className="hero"
        style={{ background: `url(${heroBg}) center/cover no-repeat` }}
      >
        <div className="hero-content">
          <h1>Seamless Intake, Instant Insights</h1>
          <p>
            Our AI-powered platform streamlines custom intake forms and delivers comprehensive summaries.
          </p>
          <form className="form-inline" onSubmit={handleSubmit}>
            <input 
              type="email" 
              placeholder="Enter your email" 
              value={email} 
              onChange={(e) => setEmail(e.target.value)} 
              required 
            />
            <button type="submit">Get Notified</button>
          </form>
        </div>
      </section>

      {/* Features Section */}
      <section className="features">
        <h2>Our Features</h2>
        <div className="feature-items">
          <div className="feature-item">
            <img 
              src={featureImg1} 
              alt="Custom Intake Forms" 
            />
            <h3>Custom Intake Forms</h3>
            <p>Tailored questions to capture relevant data.</p>
          </div>
          <div className="feature-item">
            <img 
              src={featureImg2} 
              alt="AI-Powered Summaries" 
            />
            <h3>AI-Powered Summaries</h3>
            <p>Instant insights delivered to your dashboard.</p>
          </div>
          <div className="feature-item">
            <img 
              src={featureImg3} 
              alt="Seamless Integration" 
            />
            <h3>Seamless Integration</h3>
            <p>Connect with your existing workflows effortlessly.</p>
          </div>
        </div>
      </section>

      {/* Team Section */}
      <section className="team">
        <h2>Meet Our Team</h2>
        <div className="profile-cards">
          <div className="profile-card">
            <img 
              src={teamImg} 
              alt="Team Member" 
            />
            <h3>Aryan Shah</h3>
            <p>Lead Developer</p>
          </div>
          {/* Additional team member cards can be added here */}
        </div>
      </section>

      {/* Footer */}
      <footer className="footer">
        <p>Â© 2025 Atomic. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default App;
