import React, { useState } from 'react';
import '../App.css'; // Reuse global styles if desired

function LandingPage() {
  const [email, setEmail] = useState('');
  const [status, setStatus] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus('');

    try {
      // Example: adjust URL to your Flask endpoint
      const response = await fetch('http://127.0.0.1:5000/api/subscribe', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email }),
      });
      const result = await response.json();
      setStatus(result.message);
    } catch (error) {
      console.error(error);
      setStatus('Error subscribing. Please try again.');
    }
  };

  return (
    <div className="hero-container">
      {/* Background video */}
      <video autoPlay loop muted className="background-video">
        <source src="/background.mp4" type="video/mp4" />
        Your browser does not support HTML5 video.
      </video>

      {/* Dark overlay */}
      <div className="overlay">
        {/* Navbar (optional) */}
        <nav className="navbar">
          <div className="logo">YourLogo</div>
          <ul className="nav-links">
            <li><a href="/">Home</a></li>
            <li><a href="/features">Features</a></li>
            <li><a href="#testimonials">Testimonials</a></li>
            <li><a href="#faq">FAQ</a></li>
          </ul>
          <button className="notify-btn">Notify me</button>
        </nav>

        {/* Hero content near bottom */}
        <div className="hero-content">
          <h1 className="hero-heading">
            Seamless Intake,<br />
            Instant Insights
          </h1>
          <p className="hero-subtext">
            Our company specializes in AI-powered custom dynamic intake forms that adapt to user responses
            and generate comprehensive, easy-to-read summariesâ€”streamlining decision-making
            and enhancing efficiency.
          </p>

          {/* Subscription form */}
          <form onSubmit={handleSubmit} className="subscribe-form">
            <input
              type="email"
              name="email"
              placeholder="name@email.com"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <button type="submit" className="subscribe-button">
              Get notified
            </button>
          </form>
          {status && <p className="status-message">{status}</p>}
        </div>
      </div>
    </div>
  );
}

export default LandingPage;
